import styled from 'styled-components';

export const Container = styled.div`
	max-width: 1020px;
	margin: 0 auto;
	display: flex;
	flex-direction: column;
	div.row {
		padding: 20px 0;
		display: flex;
		border-bottom: 1px solid #ccc;
		align-items: center;
		@media(max-width: 1020px) {
			flex-direction: column;
			div{
				width: 100%;
				margin: 0 35px;
			}
		}

	}
`;
